package day6

const InputStr = ""

func CharsBeforeStartOfPacket(str string) int {
	last3 := make([]rune, 3)

	for i, char := range str {
		if i < 3 {
			last3[i] = char
		} else if contains(last3, char) || !isUnique(last3) {
			last3 = append(last3[1:], char)
		} else {
			return i + 1
		}
	}
	return 0
}

func CharsBeforeStartOfMessage(str string) int {
	last13 := make([]rune, 13)

	for i, char := range str {
		if i < 13 {
			last13[i] = char
		} else if contains(last13, char) || !isUnique(last13) {
			last13 = append(last13[1:], char)
		} else {
			return i + 1
		}
	}
	return 0
}

func isUnique(s []rune) bool {
	runes := map[rune]bool{}
	for _, v := range s {
		if runes[v] {
			return false
		} else {
			runes[v] = true
		}

	}

	return true
}

func contains(s []rune, str rune) bool {
	for _, v := range s {
		if v == str {
			return true
		}
	}

	return false
}
